import './App.css';
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt, faEye } from '@fortawesome/free-solid-svg-icons';
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { Component } from 'react';

const url="https://61736fee110a74001722302b.mockapi.io/Usuarios";

class App extends Component {

  state={
    data:[],
    modalInsertar: false,
    form:{
      id: '',
      nombre: '',
      apellido: '',
      email: '',
      telefono: '',
      ciudad: '',
      pais: '',
    }
  }



  peticionGet=()=>{
    axios.get(url).then(response =>{
      this.setState({data: response.data});
    })
  }

  peticionPost=async()=>{
    delete this.state.form.id;
    await axios.post(url,this.state.form).then(response=>{
      this.modalInsertar();
      this.peticionGet();
    }).catch(error=>{
      console.log(error.message);
    })
  }

  modalInsertar=()=>{
    this.setState({modalInsertar: !this.state.modalInsertar});
  }

  handleChange=async e =>{
    e.persist();
    await this.setState({
      form:{
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
    console.log(this.state.form);
  }

  componentDidMount(){
    this.peticionGet();
  }



  render(){
    const {form}=this.state;
    return (
      <div className="App">
       <br/>
    <button className="btn btn-success" onClick={()=>this.modalInsertar()}>Agregar Usuario</button>
       <br/><br/>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Apellido</th>
              <th>Email</th>
              <th>Telefono</th>
              <th>Ciudad</th>
              <th>Pais</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map(usuario=>{
              return(
                <tr>
                  <td> {usuario.id} </td>
                  <td> {usuario.nombre} </td>
                  <td> {usuario.apellido} </td>
                  <td> {usuario.email} </td>
                  <td> {usuario.telefono} </td>
                  <td> {usuario.ciudad} </td>
                  <td> {usuario.pais} </td>
                  <td>  
                      <button className="btn btn-primary"><FontAwesomeIcon icon= {faEdit} /></button>
                      {"   "}
                      <button className="btn btn-danger"><FontAwesomeIcon icon= {faTrashAlt} /></button>
                  </td>
                </tr>
              )
            })}

          </tbody>

        </table>



        <Modal isOpen={this.state.modalInsertar}>
                  <ModalHeader style={{display: 'block'}}>
                    <span style={{float:'rigth'}}>x</span>
                  </ModalHeader>
                  <ModalBody>
                    <div className="form-group">
                      <label htmlFor="id">ID</label>
                      <input className="form-control" type="text" name="id" id="id" readOnly onChange={this.handleChange} value={this.state.data.length+1}/>
                      <br/>
                      <label htmlFor="nombre">Nombre</label>
                      <input className="form-control" type="text" name="nombre" id="nombre" onChange={this.handleChange} value={form.nombre}/>
                      <br/>
                      <label htmlFor="apellido">Apellido</label>
                      <input className="form-control" type="text" name="apellido" id="apellido" onChange={this.handleChange} value={form.apellido}/>
                      <br/>
                      <label htmlFor="email">Email</label>
                      <input className="form-control" type="text" name="email" id="email" onChange={this.handleChange} value={form.email}/>
                      <br/>
                      <label htmlFor="telefono">Telefono</label>
                      <input className="form-control" type="number" name="telefono" id="telefono" onChange={this.handleChange} value={form.telefono}/>
                      <br/>
                      <label htmlFor="ciudad">Ciudad</label>
                      <input className="form-control" type="text" name="ciudad" id="ciudad" onChange={this.handleChange} value={form.ciudad}/>
                      <br/>
                      <label htmlFor="pais">Pais</label>
                      <input className="form-control" type="text" name="pais" id="pais" onChange={this.handleChange} value={form.pais}/>
                      <br/>

                    </div>
                  </ModalBody>
                  <ModalFooter>
                    <button className="btn btn-success" onClick={()=>this.peticionPost()}>
                      Insertar
                    </button>
                    <button className="btn btn-danger" onClick={()=>this.modalInsertar()}>
                      Cancelar
                    </button>
                  </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default App;
